// Come√ßo de redirecionamento system.out.println
    private void updateTextArea(final String text) {
        SwingUtilities.invokeLater(new Runnable() {
          public void run() {
            saida.append(text);
          }
        });
    }
    
    private void redirectSystemStreams() {
        OutputStream out = new OutputStream() {
          @Override
          public void write(int b) throws IOException {
            updateTextArea(String.valueOf((char) b));
          }

          @Override
          public void write(byte[] b, int off, int len) throws IOException {
            updateTextArea(new String(b, off, len));
          }

          @Override
          public void write(byte[] b) throws IOException {
            write(b, 0, b.length);
          }
        };
 
    System.setOut(new PrintStream(out, true));
    System.setErr(new PrintStream(out, true));
  } 
// Fim do redirecionamento system.out.println
    
    private void buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarActionPerformed
      
        try {
            load.setIndeterminate(true);
            
            if (inputService.getText().equals("")) inputService.setText("detran");
            
            Button b = new Button(inputService.getText(), inputMessage.getText(), load);
            redirectSystemStreams();
            saida.setText(null);
           
            b.start();            
            
        } catch (Exception e){
            System.out.println("Try Again!");
        }

    }//GEN-LAST:event_buscarActionPerformed
    
